mapper.select:
SELECT * FROM ( SELECT ROWNUM tot_cnt, T.* FROM ( SELECT (SELECT prod_name FROM prod WHERE board.prod_id = prod.prod_id) AS prod_name , (SELECT mem_name FROM member WHERE board.mem_id = member.mem_id) AS mem_name , bd_title , bd_rating , TO_CHAR(bd_date,'YYYY-MM-DD') as bd_date , mem_id , bd_kind_id , prod_id , bd_id , bd_del FROM BOARD WHERE 1=1
AND bd_kind_id = #{bd_kind_id}
ORDER BY bd_date DESC ) T )

SELECT * FROM ( SELECT ROWNUM tot_cnt, T.* FROM ( SELECT (SELECT prod_name FROM prod WHERE board.prod_id = prod.prod_id) AS prod_name , (SELECT mem_name FROM member WHERE board.mem_id = member.mem_id) AS mem_name , bd_title , bd_rating , TO_CHAR(bd_date,'YYYY-MM-DD') as bd_date , mem_id , bd_kind_id , prod_id , bd_id , bd_del FROM BOARD WHERE 1=1
AND bd_kind_id = #{bd_kind_id}
ORDER BY bd_date DESC ) T )

mapper.insert:
INSERT INTO BOARD (   bd_id
					, bd_title
					, bd_content
					, bd_date
					, bd_del
					, bd_rating
					, bd_views
					, bd_group
					, mem_id
					, bd_parent
					, bd_kind_id
				  )
			VALUES (  #{bd_id}
			 		, #{bd_title}
			 		, RAWTOHEX(#{bd_content})
			 		, TO_DATE(SYSDATE,#{bd_date})
			 		, #{bd_del}
			 		, #{bd_rating}
			 		, #{bd_views}
			 		, #{bd_group}
			 		, #{mem_id}
			 		, #{bd_parent}
			 		, #{bd_kind_id}
			 		)
