mapper.insert:
INSERT INTO FILEDATA 
(
	  FILE_ID
	, FILE_NAME
	, FILE_UPNAME
	, FILE_PATH
	, MEM_ID
)
VALUES 
(
	  #{file_id}
	, #{file_name}
	, #{file_upname}
	, #{file_path}
	, #{mem_id}
)

mapper.delete:
DELETE FROM FILEDATA
WHERE 1=1

	
	AND  FILE_ID = #{file_id}
	
	
	AND  MEM_ID = #{mem_id}
	
	
	AND 1=2
