mapper.select:
SELECT COUNT(*)getListProdMember
  FROM MEMBER
 WHERE MEM_ID = #{mem_id}

mapper.update:
UPDATE MEMBER
   SET MEM_KIND = #{mem_kind}
	 , MEM_NAME = #{mem_name}
	 , MEM_TEL = #{mem_tel}
	 , MEM_BIRTH = #{mem_birth}
	 , MEM_GEN = #{mem_gen}
	 , MEM_ZIP = #{mem_zip}
	 , MEM_ADD = #{mem_add}
	 , MEM_ADDR = #{mem_addr}
	 , MEM_CVS_NAME = #{mem_cvs_name}
	 , MEM_CVS_TEL = #{mem_cvs_tel}
	 , MEM_INTRO = #{mem_intro}
	 , MEM_X = #{mem_x}
	 , MEM_Y = #{mem_y}
 WHERE MEM_ID = #{mem_id}

UPDATE MEMBER
   SET MEM_PW = CRYPTPACK.ENCRYPT(#{mem_pw, jdbcType=VARCHAR}, #{mem_id, jdbcType=VARCHAR})
 WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT MEM_ID
	 , MEM_KIND
	 , MEM_NAME
	 , MEM_TEL
  FROM MEMBER
  WHERE 1=1
  
    AND MEM_ID = #{mem_id}
  
  
    AND MEM_NAME = #{mem_name}
  
  
    AND MEM_TEL = #{mem_tel}

SELECT * FROM (SELECT a.*, ROWNUM rn FROM (SELECT MEM_ID , MEM_PW , MEM_KIND , MEM_NAME , MEM_TEL , MEM_BIRTH , MEM_GEN , MEM_ZIP , MEM_ADD , MEM_ADDR , MEM_CVS_NAME , MEM_CVS_TEL , MEM_INTRO , MEM_X , MEM_Y FROM MEMBER WHERE 1=1
AND MEM_KIND = #{mem_kind}
AND MEM_NAME = #{mem_name}
ORDER BY MEM_NAME ) a ) WHERE rn BETWEEN (#{page}-1)*#{pageSize}+1 AND #{page}*#{pageSize}
