mapper.select:
SELECT  *
FROM (
    SELECT T.*,ROWNUM RN
    FROM  (
        SELECT  *
        FROM PROD
        WHERE EVENT_ID = #{eventId}
    )T
)F
WHERE F.RN BETWEEN 1  AND  #{view}

SELECT * FROM ( SELECT ROWNUM cnt, a.* FROM ( SELECT (SELECT CTGY_NAME FROM CATEGORY WHERE CTGY_ID = PR_CLASS_LG) AS PR_CLASS_LG ,(SELECT CTGY_NAME FROM CATEGORY WHERE CTGY_ID = PR_CLASS_MD) AS PR_CLASS_MD ,PROD_ID ,PROD_NAME ,PROD_COST ,PROD_PRICE ,PROD_EXNUM ,(SELECT EVENT_NAME FROM EVENT WHERE EVENT_ID = PROD.EVENT_ID) AS EVENT_ID ,PROD_INTRO ,FILE_PATH ,FILE_UPNAME FROM PROD WHERE 1=1
AND PR_CLASS_LG = #{pr_class_lg}
AND PR_CLASS_MD = #{pr_class_md}
AND PROD_NAME LIKE '%'|| #{prod_name} ||'%'
AND PROD_COST BETWEEN #{cost_min} AND #{prod_cost}
AND PROD_PRICE BETWEEN #{price_min} AND #{prod_price}
)a )

SELECT  count(*) as cnt
FROM PROD
WHERE 1=1
 
 	AND PR_CLASS_LG = #{pr_class_lg}
 
 
 	AND PR_CLASS_MD = #{pr_class_md}
 
 
 	AND PROD_NAME LIKE '%'|| #{prod_name} ||'%'
 
 
 	AND PROD_COST  BETWEEN #{cost_min} AND #{prod_cost} 
 
 
 	AND PROD_PRICE BETWEEN  #{price_min} AND #{prod_price}

mapper.insert:
INSERT INTO PROD (PROD_ID, PROD_NAME, PROD_INTRO, PROD_INFO, PROD_PRICE, PROD_EXNUM, FILE_PATH, FILE_UPNAME, PR_CLASS_LG, PR_CLASS_MD, EVENT_ID, PROD_COST)
            VALUES(
            		 #{prod_id    }	
					,#{prod_name  } 
					,#{prod_intro } 
					,#{prod_info  } 
					,#{prod_price } 
					,#{prod_exnum } 
					,#{file_path  } 
					,#{file_upname} 
					,#{pr_class_lg} 
					,#{pr_class_md} 
					,#{event_id   } 
					,#{prod_cost  } 
            )

mapper.update:
UPDATE PROD 
SET    PROD_NAME 	= #{prod_name}

      ,PROD_INFO    = #{prod_info}
      ,PROD_PRICE   = #{prod_price}
      ,PROD_EXNUM   = #{prod_exnum}
      
      ,FILE_PATH    = #{file_path}
      ,FILE_UPNAME  = #{file_upname}
      
      ,PR_CLASS_LG  = #{pr_class_lg}
      ,PR_CLASS_MD  = #{pr_class_md}
      ,EVENT_ID     = #{event_id}
      ,PROD_COST    = #{prod_cost}
WHERE  PROD_ID 		= #{prod_id}
